# This file is used by a bot to 
# automatically update Open-Scoop application.
# Please do not edit this file.

# Variables
$USER = $env:USERNAME
$SCOOP = scoop which scoop
if ( !$env:SCOOP_HOME ) { 
  $env:SCOOP_HOME = resolve-path (split-path (split-path (scoop which scoop))) 
}
$checkver = "C:\\Users\\$USER\\scoop\\apps\\scoop\\current\\bin\\checkver.ps1"
$dir = "C:\\Users\\$USER\\scoop\\proj\\open-scoop" 

#Start
Set-Location $HOME
Set-Location scoop/proj/open-scoop/bin

git pull

Get-ChildItem ../.\*.json | Foreach-Object {
  $basename = $_.BaseName
  $result = ../../../apps/scoop/current/bin/checkver.ps1 -dir $dir -App $basename | Out-String
  if ($result.endswith("autoupdate available")) {
  	../../../apps/scoop/current/bin/checkver.ps1 -dir $dir -App $basename -u
  	git add $basename
  	git commit -m "Auto-updated $basename"
    Write-Output "Updated $basename"
  }
}

Write-Output "Finished updating out-of-date apps"
git pull
git push origin master
